namespace Salamek\PplMyApi;


use Salamek\PplMyApi\Enum\PackageService;
use Salamek\PplMyApi\Enum\Product;
use Salamek\PplMyApi\Exception\NotImplementedException;
use Salamek\PplMyApi\Model\IPackage;
use Salamek\PplMyApi\Model\Package;

class ZplLabel
{
    /**
     * @param IPackage[] $packages
     * @param null $decomposition
     * @return string
     * @throws \Exception
     */
    public static function generateLabels(array $packages, $decomposition = null)
    {
        if (!is_null($decomposition)) {
            throw new \InvalidArgumentException('ZplLabel::generateLabels does not support $decomposition');
        }

        $packageNumbers = [];

        $zplString = '';
        /**
         *
         *
         * @var Package $package
         */
        foreach ($packages AS $package) {
            $zplString .= self::generateLabel($package);
        }

        return $zplString;
    }

    /**
     * @param IPackage $package
     * @return string
     */
    public static function generateLabel(IPackage $package)
    {

        $zpl = '^XA';
        $zpl .= '^MUM';
        $zpl .= '^LH2,2^FS';
        $zpl .= '^LL^PW';
        $zpl .= '^PON';


        $zpl .= '^FO47,49^LRY^GB48,80,0.3,B,0^FS';
        $zpl .= '^FO13,49^LRY^GB28,80,0.3,B,0^FS';
        $zpl .= '^FO4.7,114^LRY^GB6,15,0.3,B,0^FS';
        $zpl .= '^FO47.2,107.7^LRY^GB21,21,10.5,B,0^FS';
        //ppl logo:
        $zpl .= '^FO88,7^GFA,2520,2520,9,^FS';
        $zpl .= sprintf(
            '^FO24,35^A0N,6,6^FD%s^FS',
            $package->getPackageNumber()
        );

        if (in_array($package->getPackageProductType(), Product::$cashOnDelivery)) {
            $zpl .= '^FO5,9^LRY^GB6,38,3,B,0^FS';
            $zpl .= '^FO6,10^A0R,4,4^FDDOB:^FS';
            $zpl .= sprintf(
                '^FO6,29^A0R,4,4^FD%s %s^FS',
                $package->getPaymentInfo()->getCashOnDeliveryPrice(),
                $package->getPaymentInfo()->getCashOnDeliveryCurrency()
            );
        }

        $zpl .= '^FO95,49^A0R,2,2^FDConsignee / Prijemce^FS';
        $zpl .= '^FO41,49^A0R,2,2^FDConsignor / Odesilatel^FS';
        $zpl .= sprintf(
            '^FO86,51^AFR,4^FD%s^FS',
            $package->getRecipient()->getName()
        );
        $zpl .= sprintf(
            '^FO81,51^AFR,4^FD%s^FS',
            $package->getRecipient()->getStreet()
        );
        $zpl .= sprintf(
            '^FO76,51^AFR,4^FD%s, %s^FS',
            $package->getRecipient()->getCity(),
            $package->getRecipient()->getCountry()
        );
        $zpl .= sprintf(
            '^FO60,51^A0R,8,8^FD%s^FS',
            $package->getRecipient()->getZipCode()
        );
        $zpl .= sprintf('^FO55,51^A0R,4,4^FDkontakt^FS');
        $zpl .= '^FO50,51^A0R,3,3^FDTEL.: ^FS';
        $zpl .= sprintf(
            '^FO50,59^A0R,3,3^FD%s^FS',
            $package->getRecipient()->getPhone()
        );
        $zpl .= sprintf(
            '^FO53.7,110^A0R,7,7^FD%s^FS',
            in_array(PackageService::EVENING_DELIVERY, \Salamek\PplMyApi\Model\PackageService::packageServicesToArray($package)) ? 'Veèer' : 'Den'
        );
        $zpl .= sprintf(
            '^FO35,51^AFR,4^FD%s^FS',
            $package->getSender()->getName()
        );
        $zpl .= sprintf(
            '^FO30,51^AFR,4^FD%s^FS',
            $package->getSender()->getStreet()
        );
        $zpl .= sprintf(
            '^FO25,51^AFR,4^FD%s, %s^FS',
            $package->getSender()->getCity(),
            $package->getSender()->getCountry()
        );
        $zpl .= sprintf(
            '^FO16,51^A0R,4,4^FD%s^FS',
            $package->getSender()->getZipCode()
        );
        $zpl .= sprintf(
            '^FO5,118^A0R,5,5^FD%s/%s^FS',
            $package->getPackagePosition(),
            $package->getPackageCount()
        );
        $zpl .= '^FO80,7^ACR,2^FDPevná linka: 225 331 500^FS';
        $zpl .= '^FO76,7^ACR,2^FDhttps://www.ppl.cz^FS';
        $zpl .= '^FO72,7^ACR,2^FDinfo@ppl.cz^FS';
        $zpl .= sprintf(
            '^FO66,7^ACR,2^FD%s^FS',
            ''
        );
        $zpl .= '^FO8,49^A0R,2,2^FD^FS';
        $zpl .= '^FO3,49^A0R,2,2^FD^FS';
        $zpl .= sprintf(
            '^FO20,2^BY0.4,3,32^B2N,32,N,N^FD%s%s^FS',
            $package->getPackageNumber(),
            $package->getPackageNumberChecksum()
        );

        $zpl .= '^XZ';

        return $zpl;
    }

    /**
     * @param $pdf
     * @param $package
     * @throws \Exception
     */
    public static function generateLabelFull($pdf, $package)
    {
        throw  new NotImplementedException();
    }

    /**
     * @param $pdf
     * @param $package
     * @param $position
     * @throws \Exception
     */
    public static function generateLabelQuarter($pdf, $package, $position = null)
    {
        throw  new NotImplementedException();
    }

}
